script:
let molfiles = API.getData('molfiles');
let selectedMolfiles = API.getData('selectedMolfiles');
let dataset = API.getData('dataset');
let preferences = API.getData('preferences').resurrect();

console.log('Received action:', this.action)
if (this.action) {
    switch (this.action.name) {
        case 'addAllMolecules': {
            for (let molfile of molfiles) {
                addMolfile(molfile)
            };
            break
        }
        case 'addMolecules': {
            for (let molfile of selectedMolfiles) {
                addMolfile(molfile)
            };
            break
        }
        case 'addSelectedMolecules': {
            dataset.samples = [];
            for (let molfile of selectedMolfiles) {
                addMolfile(molfile)
            };
            break
        }
        case 'removeMolecules': {
            for (let molfile of selectedMolfiles) {
                removeMolfile(molfile)
            };
            break
        }
        case 'removeAllMolecules': {
            dataset.samples = [];
            break
        }
        case 'addMolecule': {
            addMolfile(this.action.value);
            break
        }
        case 'removeMolecule': {
            removeMolfile(this.action.value);
            break
        }
    }
}


function addMolfile(molfile) {
    const index = dataset.samples
        .find((element) => element.id == DataObject.resurrect(molfile.id))
    if (index) return;
    dataset.samples.push({
        id: molfile.id,
        name: molfile.Name,
        isBiomarker: false,
        molfile: molfile.molfile,
        _highlight: molfile._highlight,
    })
};

function removeMolfile(molfile) {
    const index = dataset.samples
        .findIndex((element) => element.id == DataObject.resurrect(molfile.id)) + 1;
    if (!index) return;
    dataset.samples.splice(index - 1, 1)
}

const stdMin = parseFloat(preferences.dataset.std.min);
const stdMax = parseFloat(preferences.dataset.std.max);
const meanMin = parseFloat(preferences.dataset.mean.min);
const meanMax = parseFloat(preferences.dataset.mean.max);

dataset.samples.map((item) => item.parameters = {
    mean: meanMin + (Math.random() * (meanMax - meanMin)),
    standardDeviation: stdMin + (Math.random() * (stdMax - stdMin))
})

if (API.getData('molfiles')) molfiles.triggerChange();
API.createData('datasetSamples', dataset.samples)

let preferences = API.getData('preferences').resurrect();
let datasetSamples = API.getData('datasetSamples');
if (!datasetSamples) return;

let configurationFile = {
    seed: preferences.seed,
    noiseFactor: preferences.noiseFactor,
    nbPoints: preferences.nbPoints,
    classes: new Array(parseInt(preferences.nbClasses))
}

for (let i = 0; i < preferences.nbClasses; i++) {
    configurationFile.classes[i] =  { nbSpectra: 1 };
    configurationFile.classes[i].unitParameters = [];
    for (let sample of datasetSamples) {
        if (DataObject.resurrect(sample.isBiomarker) == true) {
            configurationFile.classes[i].unitParameters.push({
                isBiomarker: sample.isBiomarker,
                index: sample.id,
                molfile: sample.molfile
            })
        } else {
            configurationFile.classes[i].unitParameters.push({
                isBiomarker: sample.isBiomarker,
                index: sample.id,
                molfile: sample.molfile,
                distribution: {
                    name: "normal",
                    parameters: sample.parameters
                },
            })    
        }
    }
}

await API.createData('configurationFile', configurationFile);
API.doAction('updateTwig', '')
configurationFile.triggerChange()

if (! this.button) this.button='Default layer';

var layers={
    'Default layer': {
        layer: 'Default layer'
    },
    'results': {
        layer: 'results'
    },
    'create': {
        layer: 'create'
    }
}

if (layers[this.button]) {
    for (var key of Object.keys(layers)) {
        this.enableButton(key);
    }
    API.switchToLayer(layers[this.button].layer);
    if (layers[this.button].callback) {
        layers[this.button].callback();
    }
    this.disableButton(this.button);
}

const Matrix = ML.MatrixLib.Matrix;
const configurationFile = API.getData('configurationFile').resurrect();
const preferences = API.getData('preferences').resurrect();
const classes = configurationFile.classes.map((element) => element.nbSpectra)
const spectraProcessor = API.cache('spectraProcessor');

spectraProcessor.setNormalization({
        from: 0,
        to: 10,
        numberOfPoints: preferences.nbPoints
    })

const defaultOptions = {
    frequency: 400,
    from: 0,
    to: 10,
    lineWidth: 1,
    nbPoints: 256 * 4,
    maxClusterSize: 6,
    withNoise: true
};

let k=0;
let elements = [];
for (let element of configurationFile.classes[0].unitParameters) {
    await delay(0)
    statusProgressFunction((k + 1), configurationFile.classes[0].unitParameters.length);
    k++
    if (element.molfile === undefined) continue
    const molecule = OCLE.Molecule.fromMolfile(element.molfile);
    const prediction = await nmrPredictor.spinus(molecule.toMolfile(), {OCLE:OCLE});
    const simulation = SD.NMR.fromSignals(prediction, configurationFile || defaultOptions);
    elements.push(simulation.getYData());
}

let pureElements = [];
for (let i = 0; i < configurationFile.classes.length; i++) {
    for (let j = 0; j < elements.length; j++) {
        configurationFile.classes[i].unitParameters[j].index = i * elements.length + j;
        pureElements[i * elements.length + j] = elements[j];
    }
}

let result;
if (configurationFile && pureElements) {
    result = generateDataset(pureElements, configurationFile);
    result.pureElements = pureElements;
    const variationMatrix = Matrix.rand(result.dataset.rows, result.dataset.columns)
        .mul(preferences.noiseFactor);
    result.dataset = result.dataset.add(variationMatrix);
    API.createData('export', 
        Object.entries(result).map((element) => element = {
            id: element[0],
        })
    );
}




const xAxis = new Array(result.dataset.columns)
    .fill().map((item, index) => index * 10 / result.dataset.columns)
const colors = Colors.getDistinctColorsAsString(parseInt(preferences.nbClasses));
let dataset = [];
for (let i = 0; i < classes.length; i++) {
    for (let j = 0; j < classes[i]; j++) {
        spectraProcessor.addFromData({
            x: xAxis,
            y: result.dataset.getRow(i),
        }, {
            id: `sample${classes[i] * i+j}`,
            meta: {
                category: `class${i + 1}`,
                color: colors[i]
            }
        });

        dataset.push({
            label: `sample${classes[i] * i+j}`,
            category: `class${i + 1}`,
            spectrum: {
                x: xAxis,
                y: Array.from(result.dataset.data[classes[i] * i+j]),
                styles: {
                    selected: {
                        lineColor: colors[i],
                        lineStyle: 1,
                        lineWidth: 1,
                    },
                    unselected: {
                        lineColor: colors[i],
                        lineStyle: 1,
                        lineWidth: 1,
                    },
                }
            },
            _highlight: classes[i]*i+j+1
        })
    }
}

let spectraInDataset=[];
for (let spectrum of spectraProcessor.spectra) {
    console.log(spectrum.id)
    spectraInDataset.push( {
        id: spectrum.id,
        selected: true,
        pcaModel: false,
        category: spectrum.meta.category,
        color: spectrum.meta.color,
        toc: {
            value: {
                reference: spectrum.id,
                misc: {
                    id: spectrum.id,
                    ...spectrum.meta
                }
            }
        },
        _highlight: Math.random()
    })
}

API.createData('memoryInfo', spectraProcessor.getMemoryInfo());

API.createData('spectraInDataset', spectraInDataset);

API.createData('keepOriginal', false);

API.createData('toDisplay', dataset);

API.switchToLayer('results');
UI.showNotification('The artificial data set was successfully generated', 'success');

async function statusProgressFunction(currentStatus, iterationsNumber) {
    let status = currentStatus < iterationsNumber? currentStatus: 0;
    API.createData('statusProgress', {
        currentStatus: status,
        iterationsNumber: iterationsNumber
    });
};

