        // rendererOptions: "{suppressChiralText: false, suppressESR: false, suppressCIPParity: false, noStereoProblem: false, noImplicitAtomLabelColors: true, showAtomNumber: false, showBondNumber: false, inflateToMaxAVBL: true, highlightQueryFeatures: true }",
