async function onMessage(data) {
    if(data.type === 'tab.data') {
        const couchDB = data.message.couchDB;
        const uuid = data.message.uuid;
        API.cache('couchDB', couchDB);
        API.cache('uuid', uuid);
        const roc = new Roc(couchDB);
        API.cache('roc', roc);
        const userAnalysisResults = new UserAnalysisResults(roc, data.message.uuid);
        API.cache("userAnalysisResults", userAnalysisResults);
        userAnalysisResults.refresh();
        API.createData('description', '');

        const sampleToc = new Toc(roc);
        sampleToc.initializeGroupForm();
        API.cache('sampleToc', sampleToc);

        const spectraDataSet = new SpectraDataSet(roc, sampleToc, {
            defaultAttributes: {
                oplsModel: false
            }
        });
        spectraDataSet.initializeAnalysis({autoRefresh:false});
        API.cache('spectraDataSet', spectraDataSet);
    }
}

