    threshold2: secondThreshold
  });

  if (filtered[0] !== undefined){
    for (let item of filtered) {
      currentData.push(item);
    }
  }
  await API.createData('currentData', currentData)
}


function crossCorrelationFilter(matrix, array, rangesToCalculate, options = {}) {
  let {
    spectrumRange = 10,
    threshold1 = 0.9,
    threshold2 = 0.8
  } = options;

  if (threshold1 < threshold2) {
    UI.showNotification('Threshold 2 is bigger than threshold 1', 'error');
    threshold1 = threshold2;
  }

  let increment = spectrumRange / matrix[0].normalized.y.length;
  let result = [];
  if (rangesToCalculate.length !== 0) {
    for (let i = 0; i < rangesToCalculate.length; i++) {
      let targetSlice = normalization(
        getSlices(rangesToCalculate[i], array, increment)
      );
      targetSlice = new convolution.DirectConvolution(
          targetSlice.length,
          targetSlice, 
          'CUT'
      );
      let output = [];
      for (let k = 0; k < matrix.length; k++) {
        let currentSlice = normalization(
            getSlices(rangesToCalculate[i], matrix[k].normalized, increment)
        );
        let max = Math.max(...targetSlice.convolve(currentSlice));
        let outcome = (max > threshold1) ? 'case1' :
          (max > threshold2 && max < threshold1) ?
            'case2' : 'case3';
        switch (outcome) {
          case 'case1': {
            output.push(sortLevel(1, '#3BFC02', k, matrix[k], max));
            break;
          }
          case 'case2': {
            output.push(sortLevel(2, '#FCAC02', k, matrix[k], max));
            break;
          }
          case 'case3': {
            output.push(sortLevel(3, '#FC0202', k, matrix[k], max));
            break;
          }
        }
      }
      result.push({
        data: output,
        range: rangesToCalculate[i]
      });
    }
  }
  return result;
}

function getSlices(ranges, array, increment) {
  let slices = [];
  let element = ranges.joined === undefined ? 'single' : 'composed';
  switch (element) {
    case 'single': {
      for (let j = ranges.from; j < ranges.to; j += increment) {
        slices.push(array.y[parseInt(j * (1 / increment))]);
      }
      break;
    }
    case 'composed': {
      for (let g = 0; g < ranges.joined.length; g++) {
        for (let l = ranges.joined[g].from; l < ranges.joined[g].to; l += increment) {
          slices.push(array.y[parseInt(l * (1 / increment))]);
        }
      }
      break;
    }
  }
  if (slices.length % 2 == 0) slices.pop();
  return slices;
}

function sortLevel(level, color, k, matrix, max) {
  let result = {
    pass: level,
    id: matrix.id,
    color: color,
    scatterColor: matrix.meta.color,
    max: max,
    _highlight: k,
    toStocsy: false
  };
  return result;
}

function arrayNorm(A) {
  let result = Math.sqrt(multiply(A, A).reduce((a, b) => a + b));
  return result;
}

function normalization(A) {
  let norm = arrayNorm(A);
  let result = A.map((a) => a / norm);
  return result;
}

